import React, { useState, useEffect } from 'react';
import './App.css';

const GRID_SIZE = 10;

function App() {
  const [gameState, setGameState] = useState({
    beast: [0, 0],
    engineers: []
  });

  useEffect(() => {
    const fetchGameState = async () => {
      try {
        const response = await fetch('http://localhost:5000/update');
        const data = await response.json();
        setGameState(data);
      } catch (error) {
        console.error('Error fetching game state:', error);
      }
    };

    fetchGameState();
    const intervalId = setInterval(fetchGameState, 1000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="App">
      <h1>Beast Hunt Game</h1>

      <div className="game-board" style={{ display: 'grid', gridTemplateColumns: `repeat(${GRID_SIZE}, 30px)`, gap: '1px', margin: '20px auto' }}>
        {Array.from({ length: GRID_SIZE * GRID_SIZE }).map((_, index) => {
          const x = index % GRID_SIZE;
          const y = Math.floor(index / GRID_SIZE);
          let content = 'â¬œ';

          if (gameState.beast[0] === x && gameState.beast[1] === y) {
            content = 'ðŸ‘¹';
          } else {
            const engineer = gameState.engineers.find(eng => eng.position[0] === x && eng.position[1] === y);
            if (engineer) {
              content = engineer.emoji;
            }
          }

          return (
            <div key={index} style={{ width: 30, height: 30, border: '1px solid #ccc', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
              {content}
            </div>
          );
        })}
      </div>
      <div>
        <h2>Game State</h2>
        <p>Beast: ({gameState.beast[0]}, {gameState.beast[1]})</p>
        <h3>Engineers:</h3>
        <ul>
          {gameState.engineers.map((engineer, index) => (
            <li key={index}>
              {engineer.emoji} {engineer.name}: ({engineer.position[0]}, {engineer.position[1]})
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
