import React, { useState, useEffect, useCallback } from 'react';
import './App.css';
import { Clock, Pause, Play, RotateCcw } from 'lucide-react';

const GRID_WIDTH = 10;
const GRID_HEIGHT = 10;
const BOX_SIZE = 70;
const EMOJI_SIZE = 40;

export default function App() {
  const [gameState, setGameState] = useState({
    beast: [0, 0],
    engineers: [],
    walls: []
  });
  const [elapsedTime, setElapsedTime] = useState(0);
  const [isPaused, setIsPaused] = useState(false);

  const fetchGameState = useCallback(async () => {
    if (isPaused) return;
    try {
      const response = await fetch('http://localhost:5000/update');
      const data = await response.json();
      setGameState(data);
      setElapsedTime(prevTime => prevTime + 1);
    } catch (error) {
      console.error('Error fetching game state:', error);
    }
  }, [isPaused]);

  useEffect(() => {
    const intervalId = setInterval(fetchGameState, 1000);
    return () => clearInterval(intervalId);
  }, [fetchGameState]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const handlePauseResume = () => {
    setIsPaused(prev => !prev);
  };

  const handleReset = async () => {
    try {
      await fetch('http://localhost:5000/reset', { method: 'POST' });
      setElapsedTime(0);
      setIsPaused(false);
      fetchGameState();
    } catch (error) {
      console.error('Error resetting game:', error);
    }
  };

  return (
    <div className="flex flex-col items-center min-h-screen bg-gray-900 text-white p-8">
      <div className="flex bg-gray-900 text-white p-4 w-full justify-center">
        <div 
          className="game-header flex justify-between items-center"
          style={{ maxWidth: `${GRID_WIDTH * BOX_SIZE}px` }}
        >
          <h1 className="text-4xl font-bold">The Beast from 3 East</h1>
          <div className="flex items-center space-x-4">
            <div className="game-clock flex items-center">
              <Clock className="mr-2" />
              <span className="text-xl">{formatTime(elapsedTime)}</span>
            </div>
            <button 
              onClick={handlePauseResume}
              className="p-2 bg-blue-500 rounded hover:bg-blue-600 transition-colors"
            >
              {isPaused ? <Play size={24} /> : <Pause size={24} />}
            </button>
            <button 
              onClick={handleReset}
              className="p-2 bg-red-500 rounded hover:bg-red-600 transition-colors"
            >
              <RotateCcw size={24} />
            </button>
          </div>
        </div>
      </div>

      <div className="flex w-full justify-center space-x-8 mt-8">
        <div className="game-info" style={{ width: '300px', height: `${GRID_HEIGHT * BOX_SIZE + 20}px`, overflowY: 'auto' }}>
          <table className="table w-full">
            <thead>
              <tr>
                <th className="px-4 py-2">Entity</th>
                <th className="px-4 py-2">Position</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td className="border px-4 py-2">ðŸ‘¹ Beast</td>
                <td className="border px-4 py-2">
                  ({gameState.beast[0]}, {gameState.beast[1]})
                </td>
              </tr>
              {gameState.engineers.map((engineer, index) => (
                <tr key={index}>
                  <td className="border px-4 py-2">
                    {engineer.emoji} {engineer.name}
                  </td>
                  <td className="border px-4 py-2">
                    ({engineer.position[0]}, {engineer.position[1]})
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <div className="game-board-container">
          <div
            className="game-board"
            style={{
              display: 'grid',
              gridTemplateColumns: `repeat(${GRID_WIDTH}, ${BOX_SIZE}px)`,
              gridTemplateRows: `repeat(${GRID_HEIGHT}, ${BOX_SIZE}px)`,
              gap: '1px',
              padding: '10px',
              borderRadius: '8px',
              boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
            }}
          >
            {/* Game board cells */}
            {Array.from({ length: GRID_WIDTH * GRID_HEIGHT }).map((_, index) => {
              const x = index % GRID_WIDTH;
              const y = Math.floor(index / GRID_WIDTH);
              let content = '';
              let bgColor = '#4a5568';
              let initials = '';

              if (gameState.beast[0] === x && gameState.beast[1] === y) {
                content = 'ðŸ‘¹';
                initials = 'Beast';
              } else {
                const engineer = gameState.engineers.find(
                  (eng) => eng.position[0] === x && eng.position[1] === y
                );
                if (engineer) {
                  content = engineer.emoji;
                  initials = engineer.name;
                }
              }

              return (
                <div
                  key={index}
                  style={{
                    width: BOX_SIZE,
                    height: BOX_SIZE,
                    backgroundColor: bgColor,
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'center',
                    alignItems: 'center',
                    fontSize: `${EMOJI_SIZE}px`,
                  }}
                >
                  <span>{content}</span>
                  <span style={{ fontSize: '12px' }}>{initials}</span>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}
