import React, { useState, useEffect, useCallback } from 'react';
import './App.css';
import { Clock, Pause, Play, RotateCcw, ArrowRight } from 'lucide-react';

const BOX_SIZE = 90;
const EMOJI_SIZE = 40;

export default function App() {
  const [gameState, setGameState] = useState({
    beast: null,
    engineers: [],
    walls: [],
    game_over: false,
    zombie_order: [],
    beast_hidden: true,
    turn_counter: 0,
    grid_size: [],
    current_turn_entity: '',
    beast_target: null
  });

  const [elapsedTime, setElapsedTime] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const GRID_WIDTH = gameState.grid_size[0];
  const GRID_HEIGHT = gameState.grid_size[1];

  const fetchGameState = useCallback(async () => {
    if (isPaused) return;
    try {
      const response = await fetch('http://localhost:5000/update');
      const data = await response.json();
      if (data.update_occurred) {
        setGameState(data);
        setElapsedTime(prevTime => prevTime + 1);

        if (data.game_over) {
          setIsPaused(true);
        }
      }
    } catch (error) {
      console.error('Error fetching game state:', error);
    }
  }, [isPaused]);

  useEffect(() => {
    const intervalId = setInterval(fetchGameState, 100);  // Poll more frequently
    return () => clearInterval(intervalId);
  }, [fetchGameState]);


  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const handlePauseResume = () => {
    setIsPaused(prev => !prev);
  };

  const handleReset = async () => {
    try {
      const response = await fetch('http://localhost:5000/reset', { method: 'POST' });
      const data = await response.json();
      setGameState(data);
      setElapsedTime(0);
      setIsPaused(false);
    } catch (error) {
      console.error('Error resetting game:', error);
    }
  };

  const getMostRecentlyDetectedEngineer = () => {
    const detectedEngineers = gameState.engineers.filter(eng => eng.detected_turn !== null);
    return detectedEngineers.length > 0 
      ? detectedEngineers.reduce((a, b) => a.detected_turn > b.detected_turn ? a : b) 
      : null;
  };

  const mostRecentEngineer = getMostRecentlyDetectedEngineer();

  return (
    <div className="flex flex-col items-center min-h-screen bg-gray-900 text-white p-8">
      <div className="flex bg-gray-900 text-white p-4 w-full justify-center">
        <div 
          className="game-header flex justify-between items-center"
          style={{ maxWidth: `${GRID_WIDTH * BOX_SIZE}px` }}
        >
          <h1 className="text-4xl font-bold">The Beast from 3 East</h1>
          <div 
            className="game-controls flex justify-between items-center mt-4"
            style={{ maxWidth: `${GRID_WIDTH * BOX_SIZE}px` }}
          >
            <button 
              onClick={handlePauseResume}
              className="p-2 bg-blue-500 rounded hover:bg-blue-600 transition-colors"
            >
              {isPaused ? <Play size={24} /> : <Pause size={24} />}
            </button>
            <button 
              onClick={handleReset}
              className="p-2 bg-red-500 rounded hover:bg-red-600 transition-colors"
            >
              <RotateCcw size={24} />
            </button>
          </div>
          <div className="game-clock flex items-center">
            <Clock className="mr-2" />
            <span className="text-xl">{formatTime(elapsedTime)}</span>
          </div>
        </div>
      </div>

      <div className="game-content relative">
        <div className="game-board-container flex-grow">
          <div
            className="game-board"
            style={{
              display: 'grid',
              gridTemplateColumns: `repeat(${GRID_WIDTH}, ${BOX_SIZE}px)`,
              gridTemplateRows: `repeat(${GRID_HEIGHT}, ${BOX_SIZE}px)`,
              gap: '1px',
              padding: '10px',
              backgroundColor: '#2D3748', // Darker background for grid lines
              borderRadius: '8px',
              boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
            }}
          >
            {Array.from({ length: GRID_WIDTH * GRID_HEIGHT }).map((_, index) => {
              const x = index % GRID_WIDTH;
              const y = Math.floor(index / GRID_WIDTH);
              let content = '';
              let bgColor = '#4A5568';
              let initials = '';
              let isTarget = false;

              // Check if the current cell contains the target engineer
              if (mostRecentEngineer && mostRecentEngineer.position[0] === x && mostRecentEngineer.position[1] === y) {
                isTarget = true;
              }

              if (!gameState.beast_hidden && gameState.beast && gameState.beast[0] === x && gameState.beast[1] === y) {
                content = 'ðŸ‘¹';
                initials = 'Beast';
              } else {
                const engineer = gameState.engineers.find(
                  (eng) => eng.position[0] === x && eng.position[1] === y
                );
                if (engineer) {
                  content = engineer.emoji;
                  initials = engineer.name;
                }
              }

              return (
                <div
                  key={index}
                  style={{
                    width: BOX_SIZE,
                    height: BOX_SIZE,
                    backgroundColor: bgColor,
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'center',
                    alignItems: 'center',
                    fontSize: `${EMOJI_SIZE}px`,
                    border: isTarget ? '3px solid red' : '1px solid #2D3748', // Apply yellow border for target engineer
                    boxSizing: 'border-box',
                    position: 'relative',
                  }}
                >
                  <span>{content}</span>
                  <span style={{ fontSize: '12px', position: 'absolute', bottom: '2px' }}>{initials}</span>
                </div>
              );
            })}
          </div>
        </div>


      {/* New Turn Order Table */}
      <div className="turn-order-table mt-4 p-4 bg-gray-800 rounded" style={{ maxWidth: `${GRID_WIDTH * BOX_SIZE}px` }}>
          <h2 className="text-xl font-bold mb-2 text-white">Turn Order</h2>
          <table className="w-full">
            <thead>
              <tr>
                <th className="text-left">Emoji</th>
                <th className="text-left">Name</th>
                <th className="text-left">Current Turn</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>ðŸ‘¹</td>
                <td>Beast</td>
                <td>{gameState.current_turn_entity === 'beast' && <ArrowRight size={24} />}</td>
              </tr>
              {gameState.engineers.map((engineer, index) => (
                <tr key={index}>
                  <td>{engineer.emoji}</td>
                  <td>{engineer.name}</td>
                  <td>{gameState.current_turn_entity === engineer.name && <ArrowRight size={24} />}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {gameState.zombie_order.length > 0 && (
        <div className="zombie-order mt-4 p-4 bg-gray-800 rounded" 
        style={{ maxWidth: `${GRID_WIDTH * BOX_SIZE}px` }}>
          <h2 className="text-xl font-bold mb-2 text-white">Zombified:</h2>
          <ol className="list-decimal pl-5 text-white">
            {gameState.zombie_order.map((name, index) => (
              <li key={index} className="mb-1">{name}</li>
            ))}
          </ol>
        </div>
      )}
    </div>
  );
}
